lemmatizer = WordNetLemmatizer()
def preprocess(text):
    tokens = nltk.word_tokenize(text.lower())
    lemmatized_tokens = [lemmatizer.lemmatize(token) for token in tokens]
    return ' '.join(lemmatized_tokens)

preprocessed_dialogues = [preprocess(dialogue) for dialogue in questions]

vectorizer = CountVectorizer().fit(preprocessed_dialogues)  # Fit on preprocessed dialogues

def get_response(user_input):
    user_input = preprocess(user_input)
    user_vector = vectorizer.transform([user_input])  # Transform user input
    similarity_scores = cosine_similarity(user_vector, vectorizer.transform(preprocessed_dialogues))
    best_match_index = np.argmax(similarity_scores)
    # elif best_match_index%2==1:
    #     return "Okay,ask me any study related can you suggestion."
    return answers[best_match_index]